import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { Navbar as BootstrapNavbar, Nav, Button, Container } from "react-bootstrap";

export const Navbar = () => {
        const [cookies, setCookies] = useCookies(["access_token"]);
        const navigate = useNavigate();

        const logout = () => {
                setCookies("access_token", "");
                window.localStorage.clear();
                navigate("/auth");
        };

        return (
                <BootstrapNavbar expand="lg" className="navbar navbar-dark bg-dark py-3">
                        <Container className="d-flex justify-content-between align-items-center">

                                {/* Left Corner: Login/Register & Logout */}
                                <Nav className="me-auto">
                                        {!cookies.access_token ? (
                                                <Link to="/auth" className="btn btn-outline-light me-4">
                                                        Login/Register
                                                </Link>
                                        ) : (
                                                <Button onClick={logout} className="btn btn-danger me-4">
                                                        Logout
                                                </Button>
                                        )}
                                </Nav>

                                {/* Center: FOODGRAM Brand Name */}
                                <Link to="/" className="navbar-brand text-light fs-3 fw-bold mx-4">
                                        FOODGRAM
                                </Link>

                                {/* Centered Navbar Links with Proper Spacing */}
                                <Nav className="mx-auto d-flex align-items-center gap-5">
                                        <Link className="nav-link fs-5 text-light" to="/">
                                                Home
                                        </Link>
                                        <Link className="nav-link fs-5 text-light" to="/create-recipe">
                                                Create Recipe
                                        </Link>
                                        <Link className="nav-link fs-5 text-light" to="/saved-recipes">
                                                Saved Recipes
                                        </Link>
                                </Nav>

                        </Container>
                </BootstrapNavbar>
        );
};
