import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { Navbar as BootstrapNavbar, Nav, Button, Container } from "react-bootstrap";

export const Navbar = () => {
  const [cookies, setCookies] = useCookies(["access_token"]);
  const navigate = useNavigate();

  const logout = () => {
    setCookies("access_token", "");
    window.localStorage.clear();
    navigate("/auth");
  };

  return (
    <BootstrapNavbar expand="lg" className="navbar navbar-dark">
      <Container>
        {/* Login/Register & Logout Buttons on the Left */}
        <Nav className="me-3">
          {!cookies.access_token ? (
            <Link to="/auth" className="btn btn-outline-light me-2">Login/Register</Link>
          ) : (
            <Button onClick={logout} className="btn btn-danger">Logout</Button>
          )}
        </Nav>

        {/* Brand Name (FOODGRAM) */}
        <Link to="/" className="navbar-brand fw-bold text-warning">FOODGRAM</Link>

        <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />
        <BootstrapNavbar.Collapse id="basic-navbar-nav">
          {/* Centered Navbar Links with Proper Spacing */}
          <Nav className="mx-auto gap-4">
            <Link className="nav-link fs-5" to="/">Home</Link>
            <Link className="nav-link fs-5" to="/create-recipe">Create Recipe</Link>
            <Link className="nav-link fs-5" to="/saved-recipes">Saved Recipes</Link>
          </Nav>
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
};
