import React, { useEffect, useState } from "react";
import axios from "axios";
import { Card, Button, Container, Row, Col } from "react-bootstrap";

export const Home = () => {
  const [recipes, setRecipes] = useState([]);

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await axios.get("https://foodgram-xap1.onrender.com/recipes");
        console.log("Fetched Recipes:", response.data);  // âœ… Log API response
        setRecipes(response.data);
      } catch (err) {
        console.error("Error fetching recipes:", err);
      }
    };
    fetchRecipes();
  }, []);

  return (
    <Container className="mt-4">
      <h1 className="mb-4 text-center animate__animated animate__fadeInDown">ðŸ”¥ Explore Delicious Recipes</h1>

      {recipes.length === 0 ? ( // âœ… Show message if recipes are missing
        <p className="text-center text-danger">No recipes found. Try refreshing the page.</p>
      ) : (
        <Row className="g-4 justify-content-center"> 
          {recipes.map((recipe, index) => (
            <Col xs={12} sm={6} md={4} key={recipe._id} className="d-flex justify-content-center">
              <Card 
                className="mb-4 animate__animated animate__fadeInUp" 
                style={{ animationDelay: `${index * 0.1}s`, width: "22rem" }}
              >
                <Card.Img 
                  variant="top" 
                  src={recipe.imageUrl?.startsWith("http") ? recipe.imageUrl : "https://via.placeholder.com/200"} 
                  className="card-img-top" 
                  alt={recipe.name} 
                  onError={(e) => { e.target.src = "https://via.placeholder.com/200"; }} // âœ… Fallback image
                />
                <Card.Body>
                  <Card.Title>{recipe.name}</Card.Title>
                  <Card.Text>{recipe.description || "No description available."}</Card.Text>
                  <Button variant="primary">View Recipe</Button>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};
