import React from "react";
import { Link, useNavigate } from "react-router-dom";
import { useCookies } from "react-cookie";
import { Navbar as BootstrapNavbar, Nav, Button, Container } from "react-bootstrap";

export const Navbar = () => {
  const [cookies, setCookies] = useCookies(["access_token"]);
  const navigate = useNavigate();

  const logout = () => {
    setCookies("access_token", "");
    window.localStorage.clear();
    navigate("/auth");
  };

  return (
    <BootstrapNavbar expand="lg" className="navbar navbar-dark">
      <Container>
        <Link to="/" className="navbar-brand">FOODGRAM</Link>
        <BootstrapNavbar.Toggle />
        <BootstrapNavbar.Collapse className="w-100">
          {/* Navbar links stay on the left */}
          <Nav className="me-auto d-flex">
            <Link className="nav-link" to="/">Home</Link>
            <Link className="nav-link" to="/create-recipe">Create Recipe</Link>
            <Link className="nav-link" to="/saved-recipes">Saved Recipes</Link>
          </Nav>
          {/* Login/Register & Logout buttons aligned to the right */}
          <Nav className="d-flex justify-content-end">
            {!cookies.access_token ? (
              <Link to="/auth" className="btn btn-outline-light ms-3">Login/Register</Link>
            ) : (
              <Button onClick={logout} className="btn btn-danger ms-3">Logout</Button>
            )}
          </Nav>
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
};
